mapper.select:
SELECT *
FROM comment
WHERE member_id = #{memberId}
LIMIT #{offset}, #{numOfRecords}

SELECT count(*)
FROM comment
WHERE member_id = #{memberId};

mapper.update:
UPDATE comment 
SET update_date = NOW()
WHERE id = #{id}

UPDATE comment
SET good = good + 1
WHERE id = #{id}

UPDATE comment
SET good = good - 1
WHERE id= #{id}
