mapper.select:
SELECT count(*)
FROM article
WHERE member_id = #{id} AND status = "PERMANENT";

SELECT count(*)
FROM article
WHERE member_id = #{id} AND status = "TEMP";

SELECT a.*, count(c.id) as commentCnt FROM (SELECT * FROM article WHERE status="PERMANENT") a LEFT JOIN comment c ON a.id = c.article_id GROUP BY a.id
HAVING category_id = (SELECT id FROM category WHERE title = #{category})
AND nation = #{nation}
ORDER BY register_date DESC LIMIT #{offset}, #{numOfRecords}

SELECT count(*) as totalCnt FROM article WHERE status = "PERMANENT"
AND category_id = (SELECT id FROM category WHERE title = #{category})
AND nation = #{nation}

mapper.update:
UPDATE article
SET hit = hit+1
WHERE id = #{id};

UPDATE article
SET good = good + 1
WHERE id = #{id}

UPDATE article
SET good = good - 1
WHERE id = #{id}
