mapper.select:
SELECT count(*)
FROM article
WHERE member_id = #{id} AND status = "PERMANENT";

SELECT count(*)
FROM article
WHERE member_id = #{id} AND status = "TEMP";

mapper.update:
UPDATE article
SET hit = hit+1
WHERE id = #{id};

UPDATE article
SET good = good + 1
WHERE id = #{id}

UPDATE article
SET good = good - 1
WHERE id = #{id}
